import React from 'react'
import DayOfTheWeekCard from "../../../DayCard/DayOfTheWeekCard/DayOfTheWeekCard";
import DayHeader from "../../../DayCard/DayHeader/DayHeader";

export default function CalendarMode(props) {
    return (
        <div style={{display: "flex", flexDirection: "row", width: "100%"}}>
            {props.currentWeek.map(day =>
                <DayHeader>
                <DayOfTheWeekCard
                    newDatesToVerboseHandler={props.newDatesToVerboseHandler}
                    today={day}/>
                </DayHeader>)}
        </div>
    )
}
