import React, {useState} from 'react'
import classes from "./DateCards.module.css";
import MonthTeller from "../../UI/MonthTeller/MonthTeller";
import Transition from "react-transition-group/cjs/Transition";

export default function DateCards(props) {
    const [otherMonth, setOtherMonth] = useState(false);


    let todayStyle;
    if (
        new Date(props.today).getDate() === new Date().getDate() &&
        new Date(props.today).getMonth() === new Date().getMonth()
    ) {
        todayStyle = { backgroundColor: "#f56157", color: "white" };
    }

    let nonMonthDates = {};
    let monthTeller;
    if(new Date(props.today).getMonth() !== props.monthGetter) {
        nonMonthDates = {color: 'grey'};
        monthTeller = <MonthTeller monthGetter={new Date(props.today).getMonth()}
                                   newDatesToVerboseHandler={props.newDatesToVerboseHandler}/>
    }
    return(
        <div className={classes.DateWrapper}
             onMouseEnter={() => setOtherMonth(true)}
             onMouseLeave={() => setOtherMonth(false)}>
            <Transition in={otherMonth === true && new Date(props.today).getMonth() !== props.monthGetter} timeout={1000}>
                {state => {
                    let animation = {
                        transition: "z-index .5s ease-out, opacity .5s ease-out",
                        opacity:
                            state === "entering"
                                ? 1
                                : state === "entered"
                                ? 1
                                : state === "exited"
                                    ? 0
                                    : 0
                    };
                    return (
                        <MonthTeller monthGetter={new Date(props.today).getMonth()}
                                     newDatesToVerboseHandler={props.newDatesToVerboseHandler}
                                     style={animation}/>
                    )
                }}
            </Transition>
            <div className={classes.Day} style={Object.assign(nonMonthDates,todayStyle)}>
                {new Date(props.today).getDate()}
            </div>
        </div>
    )
}
