import React, {useState, useEffect} from 'react'
import classes from './CurrentTime.module.css'

export default function CurrentTime(props) {
    const [currentTime, setCurrentTime] = useState(new Date());
    useEffect(() => {
        setInterval(()=> {
            setCurrentTime(new Date())
        }, 1000)
    }, []);
    const padToTwo = number => (number <= 9 ? `0${number}` : number);
    const calculateCurrentTime = (new Date().getHours() - props.classInitialAvailableHour) * 60 + new Date().getMinutes();
    const currentTimeTopPositioning = {top: calculateCurrentTime + "px", position: "absolute"};
    let currentTimeLineWidth;
    if(props.appViewMode === "DayMode") {
        if(props.dayCardContainerWidth !== undefined) {
            currentTimeLineWidth = {width: props.dayCardContainerWidth.width + "px"};
        }
    }
    if(props.appViewMode === "WeekMode") {
        currentTimeLineWidth = {width: props.dayCardContainerWidth * 7 + "px"};
    }
    const showCurrentTime = props.classLastlAvailableHour - props.classInitialAvailableHour * 60 > calculateCurrentTime;
    console.log(showCurrentTime);
    return (
        <div>
            {showCurrentTime ?  <div style={currentTimeTopPositioning} className={classes.CurrentTimeWrapper}>
                <div className={classes.CurrentTime} >
                    {`${padToTwo(currentTime.getHours())}:${padToTwo(currentTime.getMinutes())}`}
                </div>
                <div className={classes.CurrentTimeLine} style={currentTimeLineWidth}/>
            </div> : null}
        </div>
    )
}
