import React from 'react';
import classes from "./AgendaTitles.module.css";

export default function AgendaTitles(props) {
    const renderMonthDayAndYear = `${props.newDatesToVerboseHandler(props.currentMonth, "renderMonth")} 
    ${new Date(props.currentDay).getDate()}, ${props.currentYear}`;
    const renderMonthAndYear = `${props.newDatesToVerboseHandler(props.currentMonth, "renderMonth")}, ${props.currentYear}`;
    const renderDayOfTheWeek = props.newDatesToVerboseHandler(new Date(props.currentDay).getDay(), "renderWeekDay");
    let dayOfTheWeekStyle;
    if(props.titleMode === "DayOfTheWeek") {
        dayOfTheWeekStyle = {fontSize: "1.8rem", marginBottom: "10px"}
    }
    return (
        <div className={classes.AgendaTitlesDisplay} style={dayOfTheWeekStyle}>
            {props.appViewMode === "DayMode" ?
                renderMonthDayAndYear : renderMonthAndYear}
            {props.appViewMode === "DayMode" && props.titleMode === 'DayOfTheWeek' ?
            renderDayOfTheWeek : null}
        </div>
    )
}
