import React from "react";
import classes from "./TimeTables.module.css";

export default function EmptyTables(props) {
  //ensure that there only content in the div for the
  // column that displays the hours
  let renderSingleHour;

  if (props.style) {
    let style = {
      boxShadow: "none",
      borderRadius: "0"
    };
    Object.assign(props.style, style);
    renderSingleHour = true;
  }
//1 px per minute
 const calculateCurrentTime = (new Date().getHours() - props.classInitialAvailableHour) * 60 + new Date().getMinutes();

  const currentTimeTopPositioning = {top: calculateCurrentTime + "px"};
  let currentTimeLineWidth = {width: props.dayCardContainerWidth * 7 + 5 + "px"};

  console.log(new Date().getHours());

  return (
    <div onClick={props.backdropDisplayHandler} className={classes.EmptyTableWrapper}>
      {renderSingleHour ?
          <div className={classes.CurrentTime} style={currentTimeTopPositioning}>
            {`${new Date().getHours()}:${new Date().getMinutes()}`}
            <div className={classes.CurrentTimeLine} style={currentTimeLineWidth}/>
          </div> :
      null}
      {props.tableOfAvailableHours.map(singleHour => {
        //ensure that there only content in the div for the
        // column that displays the hours
        let displaySingleHour;
        if (renderSingleHour) {
          displaySingleHour = `${singleHour}:00`;
        }
        return (
          <div
            key={singleHour}
            className={classes.EmptyTable}
            style={props.style}
          >
            {displaySingleHour}
          </div>
        );
      })}
    </div>
  );
}
