import React from 'react';
import classes from "./AgendaTitles.module.css";

export default function AgendaTitles(props) {
    let agendaTitles = null;
    switch (props.appViewMode) {
        case("DayMode") :
            agendaTitles = `${props.newDatesToVerboseHandler(props.currentMonth, "renderMonth")} 
    ${new Date(props.currentDay).getDate()}, ${props.currentYear}`;
            break;
        case("WeekMode"):
        case("CalendarMode"):
            agendaTitles = `${props.newDatesToVerboseHandler(props.currentMonth, "renderMonth")}, ${props.currentYear}`;
            break;
        case("DayOfTheWeek" && props.currentDay !== '') :
            agendaTitles = props.newDatesToVerboseHandler(new Date(props.currentDay).getDay(), "renderWeekDay");
            break;
        default:
            agendaTitles = null;
    }


    /* const renderMonthDayAndYear = `${props.newDatesToVerboseHandler(props.currentMonth, "renderMonth")}
     ${new Date(props.currentDay).getDate()}, ${props.currentYear}`;
     const renderMonthAndYear = `${props.newDatesToVerboseHandler(props.currentMonth, "renderMonth")}, ${props.currentYear}`;
     let renderDayOfTheWeek = null;*/
    let dayOfTheWeekStyle;
    if(props.appViewMode === "DayOfTheWeek" && props.currentDay !== '') {
        dayOfTheWeekStyle = {fontSize: "1.8rem", marginBottom: "10px"};
        /*renderDayOfTheWeek = props.newDatesToVerboseHandler(new Date(props.currentDay).getDay(), "renderWeekDay");*/
    }
    return (
        <div className={classes.AgendaTitlesDisplay} style={dayOfTheWeekStyle}>
            {agendaTitles}
        </div>
    )
}
